-- Notes:
--  * Adapted from user-provided specification (originally SQL Server style) to PostgreSQL.
--  * Replaced IDENTITY with GENERATED ALWAYS AS IDENTITY.
--  * Replaced GETDATE() with CURRENT_TIMESTAMP.
--  * Kept policy_number as a unique natural key (NOT a foreign key) to avoid
--    referential constraint failures because corresponding rows are not yet in policies.
--    You may later add:  ALTER TABLE insured_tb ADD CONSTRAINT fk_insured_policy
--        FOREIGN KEY (policy_number) REFERENCES policies(policy_number);
--    AFTER seeding matching policy rows (or adjust design to use policy_id).
--  * Emails in this table may duplicate or extend the users table; optional linkage via user_id is provided.
--  * Consider future denormalization: map insured_tb.user_id to users.id when an insured also has a login.
--  * Sample data below is for DEVELOPMENT / ML FEATURE ENGINEERING ONLY. Remove in production.

CREATE TABLE IF NOT EXISTS insured_tb (
    insured_id INTEGER GENERATED ALWAYS AS IDENTITY PRIMARY KEY,
    -- Optional link to application user (nullable; not all insured parties must have credentials)
    user_id INTEGER REFERENCES users(id) ON DELETE SET NULL,

    -- CORE PERSONAL DATA
    first_name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    date_of_birth DATE NOT NULL,
    gender VARCHAR(10) CHECK (gender IN ('Male', 'Female', 'Other')),
    marital_status VARCHAR(20) CHECK (marital_status IN ('Single', 'Married', 'Divorced', 'Widowed')),
    occupation VARCHAR(100),

    -- CONTACT & ADDRESS
    contact_number VARCHAR(25),
    email VARCHAR(255), -- NOT UNIQUE here to avoid conflict with users.email; add UNIQUE if business requires
    address_line_1 VARCHAR(255),
    city VARCHAR(100),
    state_province VARCHAR(100),
    zip_postal_code VARCHAR(20),

    -- POLICY & FINANCIAL LINKAGE
    policy_number VARCHAR(50) UNIQUE NOT NULL,
    annual_income NUMERIC(18,2) CHECK (annual_income >= 0),
    credit_score_segment VARCHAR(20) CHECK (credit_score_segment IN ('Low', 'Medium', 'High')),
    policy_start_date DATE,
    policy_status VARCHAR(20) DEFAULT 'Active' CHECK (policy_status IN ('Active', 'Inactive', 'Suspended')),

    -- HISTORICAL & SYSTEM DATA
    total_claims_filed INTEGER DEFAULT 0,
    last_claim_date DATE,
    registration_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP
);

-- Indexes to optimize common lookups
CREATE INDEX IF NOT EXISTS idx_insured_tb_last_name ON insured_tb(last_name);
CREATE INDEX IF NOT EXISTS idx_insured_tb_email ON insured_tb(email);
CREATE INDEX IF NOT EXISTS idx_insured_tb_policy_status ON insured_tb(policy_status);
CREATE INDEX IF NOT EXISTS idx_insured_tb_credit_segment ON insured_tb(credit_score_segment);

